UPDATE blocks SET block = '<!-- vote_console -->\n<TABLE BGCOLOR="#006699" BORDER=0 CELLPADDING=1 CELLSPACING=0 WIDTH="100%" ALIGN="center">\n    <TR>\n        <TD>\n            <TABLE BGCOLOR="%%story_mod_bg%%" BORDER=0 CELLPADDING=3 CELLSPACING=0 WIDTH="100%" ALIGN="center">\n                <TR>\n                    <TD>\n                    %%norm_font%%\n<b>Please Help.</b> The author of this story has requested editorial help from you, and the rest of the community. Please read, and post your editorial suggestions below. The author can edit at any time, so some suggestions may already have been fixed when you read them. After editing, the story will continue on to voting as usual.\n                    %%spam_form%%\n                    %%norm_font_end%%\n                    </TD>\n                </TR>\n            </TABLE>\n        </TD>\n    </TR>\n</TABLE>\n<!-- X vote_console -->' WHERE bid = 'edit_instructions';
INSERT INTO blocks (bid, block, description, category, theme, language) VALUES ('edit_instructions_abuse', '<P>\nIf this article is abusing the edit queue please indicate this by pressing the button below.', 'Message displayed to explain the spam vote while in the edit queue.', 'content', 'default', 'en');
