INSERT INTO blocks (bid, block, description, category, theme, language) VALUES ('admin_boxes_form', '<form name="boxes" action="%%rootdir%%/admin/boxes" method="post">\r\n  <table width="100%" border="0" cellpadding="0" cellspacing="0">\r\n    <tr>\r\n      <td bgcolor="%%title_bgcolor%%">%%title_font%%Edit Boxes</font></td>\r\n    </tr>\r\n    <tr><td>%%title_font%%<font color="#FF0000">%%admin_boxes_errormsg%%</font></font></td></tr>\r\n    <tr>   \r\n      <td>%%norm_font%%<b>Box:</b> %%admin_boxes_box_menu%%\r\n        <input type="submit" name="get" value="Get Box" /></font></td>\r\n    </tr><tr><td>\r\n%%norm_font%%<b>%%admin_boxes_delete_check%%</b></font>\r\n</td></tr>\r\n    <tr>\r\n      <td>%%norm_font%%<b>Box ID:</b>\r\n        <input type="text" name="boxid" value="%%admin_boxes_boxid%%" size="40" />\r\n      </font></td>\r\n    </tr>\r\n    <tr>\r\n      <td>%%norm_font%%<b>Title:</b>\r\n        <input type="text" name="title" value="%%admin_boxes_title%%" size="40" />\r\n      </font></td>\r\n    </tr>\r\n    <tr>\r\n      <td>%%norm_font%%<b>Template:</b>%%admin_boxes_template_menu%%</font></td>\r\n    </tr>\r\n    <tr>\r\n      <td><input type="checkbox" name="choose" value="1" %%admin_boxes_choose_checked%% />\r\n        %%norm_font%%Allow users to toggle box off%%norm_font_end%%\r\n      </td>\r\n    </tr>\r\n    <tr><td>%%norm_font%%<b>Description:</b></td></tr>\r\n    <tr>\r\n      <td>%%norm_font%%\r\n        <textarea cols="50" rows="3" name="description" wrap="soft">%%admin_boxes_description%%</textarea>\r\n      </font></td>\r\n    </tr>\r\n    <tr><td>%%norm_font%%<b>Content:</b></td></tr>\r\n    <tr><td>%%norm_font%%\r\n      <textarea cols="50" rows="25" name="content">%%admin_boxes_content%%</textarea>\r\n    </font></td></tr>\r\n    <tr><td>%%norm_font%%\r\n      <input type="submit" name="write" value="Save Box" /> <input type="reset" />\r\n    </font></td></tr>\r\n  </table>\r\n</form>  \r\n', 'The box editor form.', 'admin_pages', 'default', 'en');
