INSERT INTO blocks (bid,block,aid,description,category,theme,language) VALUES ('special_page_layout','<TABLE BORDER=0 CELLPADDING=0 CELLSPACING=0 WIDTH=100%>\n  <TR>\n    <TD\nBGCOLOR="%%title_bgcolor%%">%%title_font%%<B>%%title%%</B>%%title_font_end%%</TD>\n  </TR>\n  <TR><TD>&nbsp;</TD></TR>\n  <TR>\n<TD>%%norm_font%%%%content%%%%norm_font_end%%</TD>\n  </TR>\n</TABLE>','1','<P>This is the structure all pages created in the Special Pages admin tool are formatted with. The special keys are:</P>\n<DL>\n <DT>title</DT>\n  <DD>The title of the special page, as set in the Special Pages admin tool</DD>\n <DT>content</DT>\n  <DD>The content of the special page, as set in the Special Pages admin tool</DD>\n</DL>','Page Templates','default','en');
