h=2:/sid/cid/,\nlength=3:/sid/cid/tool/,\n/sid{5}/cid/tool/','Displays comments in various formats.'),('newuser','default_template','new_user',0,1,'','','/tool/','Starts the process of creating a new user.'),('special','default_template','special',0,1,'','','EVAL{\r\n  my $caller = $S->cgi->param(\'caller_op\');\r\n  my $p = {};\r\n  $p->{op}  = \'special\';\r\n  \r\n  if ($caller eq \'special\') {\r\n	$p->{page} = $path[0];\r\n  } else {\r\n    $p->{page} = $caller;\r\n  }\r\n  \r\n  return $p\r\n}','Displays special pages.'),('olderlist','default_template','olderlist',0,1,'','','','Gives a listing of older stories.'),('search','default_template','search',0,1,'','find','length=2:/type/string/,\n/string/','Everything related to searching content on the site.'),('user','default_template','edit_user',0,1,'','whois my ~.+','EVAL{\r\n  my $p = {};\r\n  if ($S->cgi->param(\'caller_op\') eq \'my\') {\r\n    unshift @path__COMMA__ $S->{NICK};\r\n  } elsif ($S->cgi->param(\'caller_op\') =~ m/^~(.+)$/) {\r\n    unshift @path__COMMA__ $1;\r\n  }\r\n  my $uid;\r\n  if ($path[0] =~ /uid:/) {\r\n    $path[0] =~ s/^uid://;\r\n    $uid = $path[0];\r\n    $path[0] = $S->get_nick_from_uid($uid);\r\n  } else {\r\n    $uid=$S->get_uid_from_nick($path[0]); \r\n  }\r\n  if ($path[1] eq \'edit\') { $path[1] = \'prefs\' }\r\n  if ($path[1] eq \'diary\') {\r\n    $p = {\r\n      op      => \'section\'__COMMA__\r\n      user    => \"$path[0]\"__COMMA__\r\n      section => \'Diary\'__COMMA__\r\n      page    => $path[2]\r\n    };\r\n  } elsif ($path[1] =~ /comment/) {\r\n    $p->{op}      = \'search\';\r\n    $p->{type}    = \'comment_by\';\r\n    $p->{string}  = $path[0];\r\n  } elsif ($path[1] eq \'ads\') {\r\n    $p->{op}      = \'adinfo\';\r\n    $p->{ad_id}   = \"uid:$uid\";\r\n  } elsif ($path[1] eq \'stories\' || $path[1] eq \'story\') {\r\n    $p->{op}      = \'search\';\r\n    $p->{type}    = \'author\';\r\n    $p->{string}  = $path[0];\r\n    } elsif ($path[1] eq \'calendar\') {\n    $p->{op}      = \'calendar\';\n    $p->{view}    = $path[2] || $S->pref(\'calendar_view\');\n    $p->{uid}     = $uid;\n    $p->{date}    = $path[4];\n  } else {\n$p->{op}      = \'user\';\n    $p->{action}  = $path[2];\r\n    $p->{tool}    = $path[1];\r\n    $p->{nick}    = $path[0];\r\n    $p->{uid}     = $uid;\r\n  }\r\n  return $p;\r\n}','Display and edit registered users.'),('section','index_template','main_page',0,1,'','','/section/page/','Lists the stories, with intros, that are in a specified section.'),('logout','blank_template','logout_box',1,1,'','','','Logs the user out and removes their session.'),('default','index_template',NULL,0,1,'','','','The template used when no other is known. \'main\' is actually the default op'),('submitrdf','default_template','submit_rdf',1,1,'','','','Walks the user through submitting an RDF feed.'),('cron','cron_template','cron',0,1,'','','','Run waiting cron jobs.'),('showad','ad_test_template',NULL,0,1,'','','/ad_id/','Displays a single advertisment.'),('submitad','submit_template','choose_submit_ad_step',0,1,'','','/step/','Start the process of submitting an ad.'),('redirect','','redirect',0,1,'','','/ad_id/','Does ad redirections to other sites, with logging.'),('adinfo','default_template','ad_info_page',0,1,'','','/ad_id/','Displays everything related to an ad.'),('ads','default_template','buyimpressions_box',1,1,'','','/distribute','Used for purchasing ad impressions.'),('hotlist','',NULL,0,1,'','','/tool/sid{5}/new_op/page/section/','Edits a user\'s story hotlist.'),('renew','default_template','renewad_box',1,1,'','','','Used to renew ads that are already running.'),('dynamic','dynamic_template','comment_dig',0,1,'','','','Same as comments, except is used for dynamic comment mode.'),('subscribe','subscribe_template','subscribe',0,1,'','','','Function for users to purchase a subscription'),('subpay','subscribe_template','subpay_type_select',1,1,'','','',''),('css','content_only_page_template','css',1,1,'','','/file/','Returns a block of CSS with the correct file type.'),('calendar','default_template','display_calendar',0,1,'','','/view/calendar/date/','monthly, weekly, daily calendar view'),('editcalendar','default_template','edit_calendar',0,1,'edit_own_calendar','','/tool/id/action/eid/','interface for managing calendars'),('event','default_template','display_event',0,1,'','','/eid/','displays a single event'),('eventsearch','default_template','event_search',0,1,'','','','event search (should one day get rolled in with all the other search stuff)'),('submitevent','default_template','submit_event',0,1,'submit_event','','/tool/id/','submit/edit an event'),('activate','default_template','activate',1,1,'','','/key/',''),('iplookup','default_template','iplookup',1,1,'edit_user','','/iplook/',''),('tag','tag_template','show_storiesbytag',0,1,'','tags','/tag/page/','Lists the stories, with intros, that are in a specified tag'),('addtag','default_template','addtag',1,1,'comment_post','','/sid{5}/',''),('tagsort','content_only_page_template','tag_page_order',1,1,'','','/switch/','');
