, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, type=\"(?i)^(text|password|checkbox|radio|submit|reset|file|hidden|image|button)$\", name, value, checked, disabled, readonly, size, maxlength, src, alt, usemap, ismap, tabindex, accesskey, onfocus, onblur, onselect, onchange, accept, align\r\nSELECT, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, name, size, multiple, disabled, tabindex, onfocus, onblur, onchange, -close\r\nOPTGROUP, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, disabled, label, -close\r\nOPTION, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, selected, disabled, label, value\r\nTEXTAREA, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, name, rows, cols, disabled, readonly, tabindex, accesskey, onfocus, onblur, onselect, onchange, -close\r\nFIELDSET, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, -close\r\nLEGEND, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, accesskey, align=\"(?i)^(top|bottom|left|right)$\", -close\r\nBUTTON, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, name, value, type, disabled, tabindex, accesskey, onfocus, onblur, -close\r\nTABLE, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, summary, width, border, frame=\"(?i)^(void|above|below|hsides|lhs|rhs|vsides|box|border)$\", rules=\"(?i)^(none|groups|rows|cols|all)$\", cellspacing, cellpadding, align=\"(?i)^(left|center|right)$\", bgcolor, -close\r\nCAPTION, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, align=\"(?i)^(top|bottom|left|right)$\", -close\r\nCOLGROUP, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, span, width, align=\"(?i)^(left|center|right|justify|char)$\", char, charoff, valign=\"(?i)^(top|middle|bottom|baseline)$\", -close\r\nCOL, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, span, width, align=\"(?i)^(left|center|right|justify|char)$\", char, charoff, valign=\"(?i)^(top|middle|bottom|baseline)$\"\r\nTHEAD, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, align=\"(?i)^(left|center|right|justify|char)$\", char, charoff, valign=\"(?i)^(top|middle|bottom|baseline)$\", -close\r\nTBODY, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, align=\"(?i)^(left|center|right|justify|char)$\", char, charoff, valign=\"(?i)^(top|middle|bottom|baseline)$\", -close\r\nTFOOT, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, align=\"(?i)^(left|center|right|justify|char)$\", char, charoff, valign=\"(?i)^(top|middle|bottom|baseline)$\", -close\r\nTR, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, align=\"(?i)^(left|center|right|justify|char)$\", char, charoff, valign=\"(?i)^(top|middle|bottom|baseline)$\", bgcolor, -close\r\nTH, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, align=\"(?i)^(left|center|right|justify|char)$\", char, charoff, valign=\"(?i)^(top|middle|bottom|baseline)$\", abbr, acis, headers, scope=\"(?i)^(row|col|rowgroup|colgroup)$\", rowspan, colspan, nowrap, bhcolor, width, height, -close\r\nTD, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, align=\"(?i)^(left|center|right|justify|char)$\", char, charoff, valign=\"(?i)^(top|middle|bottom|baseline)$\", abbr, acis, headers, scope=\"(?i)^(row|col|rowgroup|colgroup)$\", rowspan, colspan, nowrap, bhcolor, width, height, -close\r\nFRAMESET, id, class, style, title, rows, cols, onload, onunload, -close\r\nFRAME, id, class, style, title, longdesc, name, src, frameborder=\"^(1|0)$\", marginwidth, marginheight, noresize, scrolling=\"(?i)^(yes|no|auto)$\"\r\nIFRAME, id, class, style, title, longdesc, name, src, frameborder=\"^(1|0)$\", marginwidth, marginheight, scrolling=\"(?i)^(yes|no|auto)$\", align, height, width, -close\r\nNOFRAMES, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, -close\r\nHEAD, lang, dir, profile, -close\r\nTITLE, lang, dir, -close\r\nISINDEX, id, class, style, title, lang, dir, prompt\r\nBASE, href, target\r\nMETA, lang, dir, http-equiv, name, content, scheme\r\nSTYLE, lang, dir, type, media, title, -close\r\nSCRIPT, charset, type, language, src, defer, event, for, -close\r\nNOSCRIPT, id, class, style, title, lang, dir, onclick, ondblclick, onmousedown, onmouseup, onmouseover, onmousemove, onmouseout, onkeypress, onkeydown, onkeyup, -close\r\nHTML, lang, dir, version, -close\r\n','<P>This variable contains a list of all HTML tags and their possible tag elements, using the same format as the variable <B>allowed_html</B>.  This is used for Scoop\'s HTML validator and parser for the blocks and special pages.<BR>\nThis variable should generally never be changed.</P>','tarea','Stories,Comments'),('perms','edit_user,\r\nedit_special,\r\nedit_boxes,\r\nedit_vars,\r\nedit_topics,\r\nlist_polls,\r\nedit_polls,\r\nedit_sections,\r\nstory_admin,\r\nstory_post,\r\nstory_list,\r\ncomment_post,\r\ncomment_delete,\r\ncomment_rate,\r\nmoderate,\r\nattach_poll,\r\npoll_vote,\r\npoll_post_comments,\r\npoll_read_comments,\r\nview_polls,\r\nedit_groups,\r\nsuper_mojo,\r\nrdf_admin,\r\nsubmit_rdf,\r\ncron_admin,\r\nad_admin,\r\nedit_blocks,\r\nsubmit_ad,\r\nhotlist,\r\nuse_spellcheck,\r\nview_comment_ip,\r\nshow_hidden_sections,\r\nedit_own_story,\r\nedit_ops,\r\nedit_hooks,\r\neditorial_comments,\r\nupload_content,\r\nupload_admin,\r\nupload_user,\r\nview_log,\r\nsubscription_admin,\r\nallow_subscription,\r\nsuballow_group_change,\r\nedit_macros,\r\nstory_displaystatus_select,\r\nstory_commentstatus_select,\r\nmake_new_accounts,\nbypass_safe_mode,\nadmin_search,\nad_opt_out,\nedit_prefs,\nedit_events,\nedit_event_properties,\nedit_calendars,\nsubmit_event,\nupdate_own_event,\nedit_own_calendar,\ncomment_toggle,\ncomment_remove,\r\nedit_my_stories,\r\nstory_time_update,\nview_user_files,\nevade_intro_limits,\nedit_story_tags','<P>This variable contains a list of all permissions used by Scoop, in a comma-separated list, one permission per line.<BR>\nIf any perm is added or removed from this list, the change will be reflected in the list of perms in the Groups Admin Tool.  Added perms default to off for all groups.  Removed perms are only removed from the admin tool; any removed perms that had been granted to groups before removal are still granted to those groups afterward.</P>','tarea','Security'),('hooks','comment_new(sid, cid)\r\ncomment_rate(sid, cid, uid, rating)\r\nstory_hide(sid)\r\nstory_leave_editing(sid)\r\nstory_new(sid)\r\nstory_post(sid, where)\r\nstory_update(sid)\r\nstory_vote(sid, uid, vote, section_only)\r\nuser_new(nick, is_advertiser)\r\ncomment_delete(sid, cid)\r\nstory_delete(sid)\r\ncomment_toggle(sid,cid,tool)\r\nfile_upload(path)\r\nuser_confirm(nick)\npref_change(uid,pref,newvalue)','<P>This variable contains a list of hooks recognised by Scoop for use in the Hooks Admin Tool.  The format of this variable is: one hook definition per line; each definition consists of the hook name and a comma-separated list of arguments it accepts in parentheses.<BR>\nEach hook is named based on an activity that can be taken on a Scoop site.  In the Hooks Admin Tool, each of these hooks can have code attached which will run when those activities are performed.  See the Scoop Admin Guide for more details.</P>','tarea','General'),('poll_archive_age','0','<P>This variable determines how old a poll must be before it is archived.  The possible values are: 0) do not archive polls; and any positive integer) the age of a poll in days before it will be considered for archiving.  The default value is 0.<BR>\nThis variable has no effect unless the archiving system is active.</P>','num','Polls,Archive'),('archive_ratings','1','<P>This variable determines whether or not the individual ratings of a comment are archived when the comment is archived.  The possible values are: 0) do not archive the individual ratings; and 1) archive the individual ratings.  The default value is 1.<BR>\nThis variable has no effect unless the comment archiving system is configured and enabled.  For more details on archiving, see the Scoop Admin Guide.  Since a comment\'s score and number of ratings are archived with the comment and archived comments cannot be rated, the individual ratings are not required to determine the comment\'s score, it is only available for extra information.</P>','bool','Comments,Archive'),('archive_moderations','1','<P>This variable determines whether or not the voting history of a story is archived when the story is archived.  The possible values are: 0) do not archive the voting history; and 1) archive the voting history.  The default value is 1.<BR>\nThis variable has no effect unless the story archiving system is configured and enabled.  For more details on archiving, see the Scoop Admin Guide.  Since a story\'s display status (ie, front page or section) is archived with the story, the voting history is not required to determine where it should be displayed but is only available for extra information.</P>','bool','Stories,Archive'),('comment_archive_age','31','<P>This variable prevents old stories with still-active discussions from being archived.  The possible values are any positive integer or zero and represent a number of days.  The default is 31.<BR>\nThis variable should be set to a smaller value than <B>story_archive_age</B>.  If zero, stories are archived according to <B>story_archive_age</B> alone; if any positive number, stories that would otherwise be archived are not if they have a comment posted fewer days ago than the value of this variable.</P>','num','Comments,Archive'),('story_archive_age','0','<P>This variable determines the age in days of stories to be archived.  The possible values are any positive number of days.  The default value is 0 (disabled).<BR>\nThis variable has no effect unless the archive database has been created and configured properly.  See the Scoop Admin Guide for details.  The effect of this variable is modified by the variable <B>comment_archive_age</B>; stories older than the value in this variable but with comments newer than the age specified in <B>comment_archive_age</B> will not be archived.</P>','num','Stories,Archive'),('disable_story_navbar','0','<P>This variable determines whether or not the navigation bar at the bottom of stories is displayed.  The possible values are: 0) show the navigation bar; and 1) hide the navigation bar.  The default value is 0.<BR>\nThe navigation bar is displayed directly underneath the story text and provides links to the previous and next stories.  Which stories those are can be the subject of confusion among users of the site.</P>','bool','Stories'),('end_voting_threshold','-1','<P>This variable determines the number of votes a story in the voting queue must receive before the auto-post algorithm activates and determines if and where to post the story.  The possible values are any positive integer number, or any positive percentage.  The default value is -1.<BR>\nThis variable has no effect unless the variable <B>use_auto_post</B> is set to 1.  If the variable <B>auto_post_use_time</B> is set to 1, this variable is ignored and a time-based limit is used to activate the auto-post algorithm instead.</P>','num','Stories'),('use_alternate_scoring','0','<P>This variable determines which scoring calculations are used in the story voting queue.  The possible values are: 0) compare the story\'s total score to the thresholds; and 1) compare the number of positive and negative votes to the thresholds separately.  The default value is 0.</P>','bool','Stories'),('front_page_ratio','0.50','<P>This variable determines whether a posted story should be placed on the front page or in the section page only.  The possible values are any decimal number between 0 and 1.  The default value is 0.50.<BR>\nThe number of front page votes is divided by the total number of positive votes to provide the ratio of front page votes.  If the calculated ratio is less than the value of this variable, the article is posted to the section.  This variable only takes effect if a story is voted up through the normal voting queue, and not if the story is posted using the auto-post algorithm.</P>','num','Stories'),('rdf_creator','','<P>This variable contains the name of the creator of your site\'s RDF feed.  The possible values are any string.  The default value is blank.<BR>\nThis variable has no effect unless the site is generating RDF feeds for syndication; see the Scoop Admin Guide for details.  If this variable is blank, the text in the variable <B>sitename</B> is used instead.</P>','text','RDF'),('rdf_publisher','','<P>This variable sets the identity of the publisher of the RDF file.  The possible values are any short string.  The default value is blank.<BR>\nThis variable has no effect unless the site is generating RDF feeds for syndication; see the Scoop Admin Guide for details.  If this is blank, the value of the variable <B>sitename</B> is used in its place.</P>','text','RDF'),('rdf_fetch_timeout','60','<P>This variable determines how long your site will wait before giving up when fetching another site\'s RDF feed.  The possible values are any positive number of seconds.  The default value is 60.<BR>\nThis variable has no effect unless the site is fetching RDF feeds for display; see the Scoop Admin Guide for details.</P>','num','RDF'),('hide_diary_search','0','<P>This variable determines whether or not diaries can be searched using Scoop\'s search form.  The possible values are: 0) allow diary searching; and 1) do not allow diary searching.  The default value is 0.<BR>\nThis variable has no effect unless the variable <B>use_diaries</B> is set to 1.</P>','bool','Stories'),('hide_disabled_comments','0','<P>This variable determines whether or not comments to stories with comments disabled can be found in a comment search.  The possible values are: 0) allow disabled comments in search results; and 1) hide disabled comments in search results.  The default value is 0.<BR>\nStories with comments disabled don\'t display any comments or the comment controls when the story is viewed.</P>','bool','Search'),('hide_unposted_comments','0','<P>This variable determines whether or not comments from stories not published to either the front of any section are displayed in search results.  The possible values are: 0) comments attached to stories in the queue or hidden are shown in search results; and 1) comments attached to stories in the queue or hidden are not shown in search results.  The default value is 0.<BR>\nThis variable only applies to search results; comments on hidden stories can still be viewed when this variable is set to 1.</P>','bool','Search'),('carry_comment_titles','0','<P>This variable determines whether or not comments require new subjects or use the parent comment as a source.  The possible values are: 0) the comment\'s subject line is empty when a user clicks on the reply link; and 1) the comment\'s subject line contains \"Re: (previous subject line)\" when a user clicks on the reply link.  The default value is 0.<BR>\nIf the parent comment already has a subject beginning with \"Re:\", Scoop does not add another one.  If the user has changed the subject line, Scoop does not override it with the parent\'s subject.</P>','bool','Comments'),('use_initial_rating','0','<P>This variable determines whether or not newly posted comments are given an initial rating.  The possible values are: 0) do not give an initial rating; and 1) give an initial rating.  The default value is 0.<BR>\nThe initial rating is completely overridden by the first real rating given by another user, unless the variable <B>real_initial_rating</B> is set to 1.  The actual value of the initial rating given is set in the variables <B>anonymous_default_points</B> and <B>user_default_points</B>.</P>','bool','Comments'),('anonymous_default_points','2','<P>This variable sets an anonymously posted comment\'s initial value.  The possible values are any number between the values set in <B>rating_min</B> and <B>rating_max</B>, inclusive.  The default value is 2.<BR>\nThis variable has no effect unless the variable <B>use_initial_rating</B> is set to 1.<BR>\nThe value is used as an initial comment rating on all comments posted anonymously; a single rating by a user will cause the initial rating to vanish because it is not an actual comment rating.  It is merely used to give unrated comments a rank when comments are sorted by rating.</P>','num','Comments'),('user_default_points','3','<P>This variable determines the initial rating all registered users will receive on their comments.  The possible values are any number between <B>rating_min</B> and <B>rating_max</B>.  The default value is 3.<BR>\nThis variable has no effect unless the variable <B>use_initial_rating</B> is set to 1.</P>','num','Comments'),('recent_topics_num','0','<P>This variable determines how many topic images are shown in the (slashdot-style) list of most recent story topics.  The possible values are any positive integer.  The default value is 0.<BR>\nFIXME: The box displaying the recent topic images is not included in Scoop by default.  It seems to have been lost.</P>','num','Stories'),('show_threshold','0','<P>This variable determines whether or not the post and hide thresholds are displayed in the story moderation queue.  The possible values are: 0) do not show the thresholds; and 1) show the post and hide thresholds.  The default is 0.<BR>\nThe thresholds are taken from the variables <B>post_story_threshold</B> and <B>hide_story_threshold</B>.  If one or both of those thresholds are percentages, the numeric value at the time of the page request is calculated and displayed instead of the percentage.</P>','bool','Stories'),('sections_excluded_from_all','Diary, advertisements, events','<P>This variable determines which sections will not be shown in the \"Everything\" pseudo-section or the RDF feed.  The possible values are a comma-separated list of section names.  The default list contains the Diary and adverts sections.</p>','text','Stories'),('story_nav_bar_sections','Diary, advertisements','<P>This variable lists the sections whose story navigation bar should only contain other stories from that section. The possible value is a comma-separated list of section names. The default value is the diary and ad sections.<BR>This only has an effect if the variable <B>disable_story_navbar</B> is set to 0. If the current story is not from one of the sections listed, then only stories also not in this list are considered when building the story navbar. If the current story is from one of the sections listed, then only stories also in that one section are considered when building the story navbar.</P>','text','Stories'),('diary_topics','0','<P>This variable determines whether or not users can select topics other than `diary\' for their diaries. The possible values are: 0) users may not select a topic; and 1) users may select any topic available to regular stories. The default value is 0.</P>','bool','Stories'),('anon_user_nick','Anonymous Hero','The nickname which will be used for the default anonymous user.','text','General'),('anon_user_group','Anonymous','The group which the standard anonymous user is a part of (most likely, this will be \"Anonymous\").','text','General'),('blocked_domains','example.com,\r\nexample.net','<P>This variable contains a comma-separated list of domains that may not be used to create a new account with. The default value lists the ever-popular `nospam.com\' and the by-definition-invalid `example.com\'.<BR>If a user tries to create an account with an email address in one of the blocked domains, he is told so and cannot create an account with that address.</P>','tarea','Security'),('rating_labels','','<P>This variable replaces the numeric values for ratings with text labels. The numbers are still there behind the scenes, but this allows you to specify right in the rating form what the different numbers mean. The possible values are a comma-separated string of values, from lowest to highest (including the \'hide\' value for trusted users as the first entry). The default value is blank, meaning numbers only are used in the rating form.<BR>\nSo users know what the averaged score means in relation to the text labels, it\'s a good idea to include the numeric value in the text label.</P>','text','Comments'),('minimum_ratings_to_count','1','<P>This variable determines the number of ratings that must be done before the comment\'s rating is calculated and its value applied to a users mojo score. The possible values are any positive number. The default value is 1.<BR>\nThis can prevent a single malicious vote from hiding a comment if set above 1.</P>','num','Comments'),('filter_ratings_by_ip',NULL,'<P>This variable determines whether or not multiple ratings from the same client IP address are counted when determining a comment\'s score and a user\'s mojo. The possible values are: 0) allow any number of ratings from one IP; and 1) only count the first rating from any given IP address. The default value is 0.<BR>\nBefore turning this variable on, consider that there are several situations in which multiple ratings may appear to come from the same IP. One is the case of a lot of computers behind a firewall; all of the computers will appear to come from one IP even though there are many people and many computers. Another is the case of a computer lab, where many users may log on or off of any number of computers at different times, and two different users may rate the same comment from the same computer. This variable does not affect the normal behaviour of only allowing one rating per account.</P>','bool','Comments'),('hide_rating_value',NULL,'<P>This variable sets the numeric value of the hide rating. The possible values are any number below <B>rating_min</B>. The default value is blank.<BR>\nIf this variable is blank or zero, a value one below <B>rating_min</B> is used.</P>','num','Comments'),('safe_mode','0','<P>This variable determines whether or not non-Superusers will have access to the site.  The possible values are 0) everybody has access, as normal; and 1) only accounts in the <B>Superuser</B> group will be allowed normal access.  The default value is 0.<BR>\nThis variable should only be set to 1 when you are making changes to the site or upgrading the code, and want to deny general access while still having access yourself.  When this variable is set to 1, all non-Superuser accounts will be sent 503 Service Unavailable, which you can then configure however you like via Apache\'s ErrorDocument directive.  If set to 1, make sure not to log out, or you will have to change it back to 0 via the mysql shell.  The group which can access the site when this variable is active is not configurable.</P>','bool','Security'),('main_op_eval','0','<P>This variable determines whether or not the \'main\' op (front page request, no path) will allow an EVAL block in its URL Templates field. The possible values are: 0) do not allow EVALs; and 1) allow EVALs. The default value is 0.<BR>\nThis is useful if you have subdomains that you want to have different front pages, such as using news.mysite.org instead of www.mysite.org/section/news, or nickname.mysite.org instead of www.mysite.org/user/nickname/diary. To use the domain name as part of the path, the variable <B>use_host_parse</B> must be turned on. See the Scoop Admin Guide for details on Ops and how they use EVAL blocks.</P>','bool','Ops'),('use_host_parse','0','URL (op) Templates can be constructed that make use of a special array @host, which contains fragments of the hostname used in the HTTP request. $host[0] contains the FQDN. the other array elements contain 1 or more elements of the hostname, split from left to right. These tokens can be used to set param values within URL (op) Templates.\r\nExample for \'main\' OP:   /op=section/page=$host[1]/\r\ncauses display of section based on first host fragment','bool','Ops'),('use_whosonline','0','<P>This variable determines whether or not visitors should be tracked for the \"who\'s online?\" box. The possible values are: 0) do not track visitors; and 1) track visitors. The default value is 0.<BR>\nThis only keeps user ID, IP address, and a timestamp, it does no other hit tracking. It is really only useful for seeing when a user last requested a page from your site.</P>','bool','General'),('payment_use_cc','0','<P>This variable determines whether or not Scoop will provide a link to pay for ads or subscriptions via credit card. The possible values are: 0) do not offer credit card payments as an option; or 1) offer credit card payments as an option. The default value is 0.<BR>\nTo use credit card payments, you must have an SSL-enabled webserver, a certificate, and a credit card merchant account. Scoop currently only supports the Linkpoint credit card processor, which requires a proprietary module. If you want to use credit card payments through scoop, email rusty@kuro5hin.org for help, he has very reasonable rates.</P>','bool','Advertising'),('payment_use_paypal','0','<P>This variable determines whether or not Scoop will provide a link to pay for ads or subscriptions via paypal. The possible values are: 0) do not offer paypal as an option; or 1) offer paypal as an option. The default value is 0.<BR>\nTo use paypal payments, you must have a paypal account. If you will be doing any kind of traffic, you will probably find that their free account is not sufficient, and you have to go with one of their pay accounts.</P>','bool','Advertising'),('paypal_business_id','paypal@example.com','<P>This variable contains the email address associated with your Paypal account and is used to receive payments from advertising or subscriptions. The default value is an invalid email address.<BR>To set up payment processing, see the Scoop Admin Guide.</P>','text','Advertising,General'),('hide_story_allowed_html','0','Set true to suppress the Allowed html line in the in story editing form. Useful for sites where users will likely just be confused by that line.','bool','Stories'),('hide_comment_allowed_html','0','Set true to suppress the Allowed html line in the in comment posting form. Useful for sites where users will likely just be confused by that line.','bool','Comments'),('safe_mode_redirect','','This variable determines which page a visitor will be redirected to when the variable <B>safe_mode</B> is turned on. It must contain the fully-qualified URL of a page outside of Scoop\'s control.<BR>\nIf left blank, Scoop will return a 503 Service Unavailable status. The page returned with that error code can be customized as described in the <B>safe_mode</B> site control description.<BR>\nUsing safe_mode_redirect, the user\'s browser will move to a different page than the one they requested, but requires no changes to Apache\'s configuration file. Leaving this blank, the user\'s browser will stay on the page they requested but display the 503 ErrorDocument as set in Apache\'s configuration file.','text','Security'),('new_user_email_from','','<P>This variable is used as the from email address used by Scoop when sending user creation emails. The possible values are any valid email address, or blank. If blank, the email address set in the variable <B>local_email</B> will be used instead.<BR>\nThis email address should probably be one you check regularly.  Since members are sent email from this address, they are likely to think that they can send to it as well, if they have any questions.  If this email address is not valid, nobody will be able to create accounts.</p>','text','General'),('allow_multiple_choice','0','<P>This variable determines whether or not users will be allowed to create multiple-choice polls (checkboxes) as well as single choice (radio).</P>','bool','Polls'),('use_alt_image_dir','1','<P>This variable determines whether or not Scoop will use the alternate image servers listed in the variable <B>image_mirrors</B>. Turning this on when there are no alternate image servers listed will have no effect.</p>','bool','General'),('use_ratings','1','<P>This variable turns the entire rating system on and off. FIXME: not implemented yet.</P>','bool','Comments'),('image_mirrors','Australia,http://au.example.com/images\r\nEurope,http://www.bigmirrorsite.fi/scoopsite/images','<P>This variable defines the names and URLs of any alternate image servers you may have, so the users can choose an image server closer to them if necessary. This variable has no effect unless the variable <B>use_alt_image_dir</B> is set to 1.</P>\r\n\r\n<P>The format for this variable is one image mirror per line; each line consists of a descriptive name, a comma, and the URL of the mirrored image directory, without a trailing slash. The default value is invalid.</P>\r\n\r\n<P>The default value for the image directory is set in the User Preferences Admin Tool, in the \'imagedir\' preference.</P>','tarea','General'),('first_login_page_order','User Info','<P>This variable determines which prefs page(s) the user sees first when first logging in, if the variable <B>show_prefs_on_first_login</b> is set. The possible values are any list of valid prefs page names, separated by commas. The default value is \'Protected, User Info\'. This value is strongly recommended as it encourages the user to change his password immediately.</p>','text','General'),('allow_event_stories','1','<P>This variable determines whether or not events can have stories attached to them. The possible values are: 0) events cannot have stories attached; and 1) events can have stories attached.<BR>Events can have multiple stories attached. Event stories are stored in the section named in <B>event_story_section</B>. Whether or not individual users can attach a story to an event is determined by the specific event\'s permissions. This variable has no effect unless <B>use_calendars</B> is on.</P>','bool','Events'),('allow_personal_calendar_view','0','<P>This variable determines whether or not users may have their own personal calendar views, and may subscribe to other calendars. The possible values are: 0) require explicit calendars to be requested; and 1) users may have a personalized calendar view which combines several calendars\' events.<BR>If <B>allow_user_calendars</B> is on or if you are setting up more than one site calendar, turning this on is recommended. If this is off, the calendar named in <B>default_calendar_id</B> is used if no calendar is explicitly selected. This variable has no effect unless <B>use_calendars</B> is on.</P>','bool','Events'),('allow_user_calendars','0','<P>This variable determines whether or not users can have their own individual calendars. The possible values are: 0) only admin-created calendars are permitted; and 1) users may create their own calendars as well.<BR>This variable has no effect unless <B>use_calendars</B> is on.</P>','bool','Events'),('use_calendars','1','<P>This variable determines whether or not the entire event calendar system is used at all. The possible values are: 0) do not use calendars; and 1) use calendars.<BR>See all of the other Site Controls in the <B>Events</B> category for further configuration.</P>','bool','Events'),('calendar_navigation_range','2','<P>The number of navigation elements on either side of \"today\" in the calendar views. The possible values are any positive integer. The default value is 4.<BR>The value chosen should be big enough to allow the user to easily navigate the calendar, but small enough that it doesn\'t wrap the navigation in ugly ways. This variable has no effect unless <B>use_calendars</B> is on.</P>','num','Events'),('default_calendar_id','1','<P>This variable sets the calendar ID number to be used when no calendar ID is specified on the event submission form. The default value is 1 (the site global calendar, included by default).<BR>This variable has no effect unless <B>use_calendars</b> is on.</p>','num','Events'),('event_search_num_options','3','<P>This variable sets the number of different event properties a user can use in one event search query. The possible values are any positive integer. The default value is 3.<BR>This should be set to a number high enough to allow reasonably flexible searching, but not so high that the search form is over-complicated.</P>','num','Events'),('event_story_section','events','<P>The name of the section used for stories attached to events. The possible values are any internal section name.<BR>If the section named here does not exist, event stories won\'t be posted properly. This variable has no effect unless <B>use_calendars</B> and <B>allow_event_stories</B> are on.</P>','text','Events'),('date_format_default','%a %b %d, %Y at %r |zone|','<p>This var can be used to change the formatting style of default dates, such as those displayed in story and comment info. If you\'re happy with the way Scoop does them, then leave this blank. If you want to change them, you may put in a formatting template here which uses the date formatting strings of your database. The formatting codes for mysql are all described here: <a href=\"http://dev.mysql.com/doc/mysql/en/Date_and_time_functions.html\">http://dev.mysql.com/doc/mysql/en/Date_and_time_functions.html</a> (scroll down to the DATE_FORMAT() function). For Postgres, prehaps someone could add a link here to those codes.</p>\r\n\r\n<p>For example, the Scoop default formatting template for mysql would be: \"%a %b %D, %Y at %r |zone|\" (or %T instead of %r if you have \"time_24h_format\" set). Note that this will override time_24h_format if you change it -- Scoop will just use whatever you put here.</p>\r\n\r\n<p><b>Special keys:</b></p>\r\n<ul>\r\n<li>|zone|: will be replaced by the user\'s time zone code as a string.\r\n</ul>','text','General'),('date_format_short','','<p>This var can be used to change the formatting style of short dates, such as those displayed in search results, the admin story list, and a couple other places. If you\'re happy with the way Scoop does them, then leave this blank. If you want to change them, you may put in a formatting template here which uses the date formatting strings of your database. The formatting codes for mysql are all described here: <a href=\"http://dev.mysql.com/doc/mysql/en/Date_and_time_functions.html\">http://dev.mysql.com/doc/mysql/en/Date_and_time_functions.html</a> (scroll down to the DATE_FORMAT() function).</p>\r\n\r\n<p>For example, the Scoop default formatting template for mysql would be: \"%m/%d/%Y %r |zone|\" (or %T instead of %r if you have \"time_24h_format\" set).</p>\r\n\r\n<p><b>Special keys:</b></p>\r\n<ul>\r\n<li>|zone|: will be replaced by the user\'s time zone code as a string.\r\n</ul>','text','General'),('date_format_wmd','','<p>This var can be used to change the formatting style of \"week month day\" dates, such as those produced by the \"titlesonly\" mode of getstories(). To be honest, I\'m not sure where these are used. If you\'re happy with the way Scoop does them, then leave this blank. If you want to change them, you may put in a formatting template here which uses the date formatting strings of your database. The formatting codes for mysql are all described here: <a href=\"http://dev.mysql.com/doc/mysql/en/Date_and_time_functions.html\">http://dev.mysql.com/doc/mysql/en/Date_and_time_functions.html</a> (scroll down to the DATE_FORMAT() function).</p>\r\n\r\n<p>For example, the Scoop default formatting template for mysql would be: \"%W %M %D\".</p>\r\n\r\n<p><b>Special keys:</b></p>\r\n<ul>\r\n<li>|zone|: will be replaced by the user\'s time zone code as a string.\r\n</ul>','text','General'),('signup_with_password','1','Collect a user\'s desired password on signup? If set to 1, the signup form will also include a password field. The initial account email will send a link which includes a key rather than a random password. This link will take a user directly to their prefs page (logging them in and activating the account) without the user needing to explicitly log in, and will also enable the password they chose on signup for future logins.\r\n<p>\r\nThis should actually be more secure than the random password method, since if the initial email is intercepted, the attacker still won\'t know the actual account password, and thus will be unable to hijack the account. It should also be easier for users to deal with.\r\n<p>\r\nIf you are upgrading from an older version of Scoop, and wish to enable this feature, you\'ll want to edit several of the blocks in the Accounts category, to remove the references to changing your password. You\'ll also need to add the box \"signup_password\" to the block new_user_html, and replace the username and password lines in the new_user_email block with the link \"|url|/activate/|pass|\".\r\n','bool','Security'),('use_diaries','1','Set to one to use diaries on this site.','bool','Stories'),('tag_threshold_classes','1,tag-popularity-1\r\n5,tag-popularity-5\r\n10,tag-popularity-10\r\n15,tag-popularity-15\r\n','Tag Count Threshholds for altering the font styling for each tag on the all tags page depending on what story count threshold it meets. Each threshold is on a line by itself, and consists of two parts: the number of stories associated with a given tag, and the CSS class name used for that tag. The styling of the CSS classes named here is done in the block <B>main_css</B>.','tarea','Tags'),('use_tags','0','Turns the \"tag\" categorization system on or off. Tags are user-input freeform story categories. Any story can have any number of associated tags. Tags are input as a simple comma-delimited string of words, and can only consist of alphanumeric characters (a-z, A-Z, 0-9).','bool','Tags'),('shared_db_tables','users userprefs pref_items','Space Seperated list of tables to be retrieved from <b>master_db</b> if <b>use_shared_database</b> is true','text','Shared Database'),('use_shared_db','0','Enable use of shared user accounts across two mysql databases on the same server.','bool','Shared Database'),('shared_db_name','scoop','Name Of the database from which to read the tables listed in <b>shared_tables</b>','text','Shared Database'),('max_title_len','100','The maximum length in characters a title can be.','num','Stories'),('min_intro_chars','0','The minimum number of characters that can be used in a story. The default is 0, which disables use of this. It works much the same as min_intro_words. Only one of the two should be used.','num','Stories'),('min_intro_words','300','The minimum number of words that can be used in a story. The default is 300. 0 disables this feature. It works much the same as min_intro_chars. Only one of the two should be used.','num','Stories'),('use_editorial_comments','0','Use editorial type comments at all. If unchecked, we won\'t include anything that refers to ed comments.','bool','Comments'),('require_tags','0','If this var is set, users <b>must</b> submit at least one tag when they submit a story.','bool','Tags'),('tu_perms','zero_rate','Comma seperated list of special permissions that trusted users can have. \"zero_rate\" is the default provided, but others can be added\nas well.','text','Security');
